import re
import spacy

# Load Catalan model (install first: pip install ca_core_news_sm)
nlp = spacy.load("ca_core_news_sm")

def count_syllables(word):
    """Approximate syllable count using vowel groups."""
    word = word.lower()
    return len(re.findall(r'[aeiou√†√®√©√≠√≤√≥√∫√º]+', word))

def flesch_szigriszt(text):
    doc = nlp(text)
    sentences = list(doc.sents)
    words = [token.text for token in doc if token.is_alpha]
    
    n_sentences = len(sentences)
    n_words = len(words)
    n_syllables = sum(count_syllables(w) for w in words)
    
    if n_sentences == 0 or n_words == 0:
        return None  # avoid division by zero

    IFSZ = 206.835 - (n_words / n_sentences) - 62.3 * (n_syllables / n_words)
    return round(IFSZ, 2)

